package operators_task;

public class between_int_double_int {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int    a= 30;
		double b= 15.77;
		int    c= 10;
		
		System.out.println("Arthimetic Operators---------------");
		System.out.println("Addition a+b+c= "+(a+b+c));
		System.out.println("Substraction a-b-c= "+(a-b-c));
		System.out.println("Multiplaction a*b*c= "+(a*b*c));
		System.out.println("division a/b/c= "+(a/b/c));
		System.out.println("Reminder a%b%c= "+(a%b%c));
		
	
	}

}

			

